var SUMMARY = 'Korekta galerii 11 ujęć';

var bot = require('../lib/bot').bot;

var client = new bot({
	server: 'poznan.wikia.com',
	//debug: true
});

// log in
client.logIn('Pyrabot', 'pyr4pyr4', function(data) {
	client.getPagesInCategory('11 ujęć', function(pages) {

		var limit = 5;

		pages && pages.forEach(function(page) {
			// ignore pages outside main namespace
			if (page.ns != 0) {
				return;
			}

			if (limit-- == 0) {
				return;
			}

			client.getArticle(page.title, function(content) {
				var gallery = '<gallery captionalign="left">';
				if (content.indexOf(gallery) === -1) {
					return;
				}

				// formatowanie galerii
				content = content.replace(
					'<gallery captionalign="left">',
					'<gallery captionalign="left" orientation="landscape" widths="200" columns="3" bordercolor="#ffffff" bordersize="large" spacing="small" captionposition="within" captionsize="small">');

				// usuń linkowania
				content = content.replace(/jpg\|(http|link).*$/mg, 'jpg');

				console.log('\n\n================================\n' + page.title + '\n================================');
				console.log(content);

				// and save it
				/**/
				client.edit(page.title, content, SUMMARY, function(data) {
					console.log('\n\n> ' + page.title + ' edited!');
				});
				/**/
			});
		});
	});
});
