var SUMMARY = 'Dodano nawigację',
	//template = "{{Nawigacja Dworce kolejowe}}",
	//category = 'Dworce kolejowe';
	template = '{{Nawigacja Pętle tramwajowe}}',
	category = 'Pętle tramwajowe';

var bot = require('../lib/bot').bot;

var client = new bot({
	server: 'poznan.wikia.com',
	//debug: true
});

// log in
client.logIn('Pyrabot', 'pyr4pyr4', function(data) {
	client.getPagesInCategory(category, function(pages) {
		var i = 0;

		pages && pages.forEach(function(page) {
			// ignore pages outside main namespace
			if (page.ns != 0) {
				return;
			}

			client.getArticle(page.title, function(content) {
				// dodano już szablon
				if (content.indexOf(template) > -1) {
					return;
				}

				// tylko bramy Twierdzy Poznań
				if (content.indexOf('Kategoria:Twierdza') === -1) {
					//return;
				}

				// dodaj szablon
				content += "\n\n" + template;

				console.log('\n\n================================\n' + page.title + '\n================================');
				console.log(content);

				// and save it
				/**/
				client.edit(page.title, content, SUMMARY, function(data) {
					console.log('\n\n> ' + page.title + ' edited!');
				});
				/**/
			});
		});
	});
});
