var SUMMARY = 'Dodano kody pocztowe',
	regexp = /\s\|kody\s?=\s\|/;

var bot = require('../lib/bot').bot;

var client = new bot({
	server: 'poznan.wikia.com',
	//debug: true
});

// log in
client.logIn('Pyrabot', 'pyr4pyr4', function(data) {
	console.log('Logged in as ' + data.lgusername + ' (session ID: ' + data.sessionid + ')');

	var db = JSON.parse( require('fs').readFileSync('../../db.json', 'utf-8') );

	//console.log(db); return;

	client.getPagesInCategory('Brak kodÃ³w pocztowych', function(pages) {
		var i = 0;

		pages && pages.forEach(function(page) {
			// ignore pages outside main namespace
			if (page.ns != 0) {
				return;
			}

			i++;

			//if (i>250) return;

			client.getArticle(page.title, function(content) {
				console.log("\n" + page.title);

				if (!regexp.test(content)) {
					console.log('No template entry!');
					return;
				}

				// pobierz kod pocztowy
				var postcodes = db[ page.title.replace(/^Ulica /, '').toLowerCase() ];

				if (!postcodes) {
					console.log('No postcode entry!');
					return;
				}

				// dodaj kody pocztowe
				content = content.replace(regexp, "\n|kody=" + postcodes.join(',') + "\n|");

				console.log('\n\n================================\n' + page.title + '\n================================');
				console.log(content);

				// and save it
				/**/
				client.edit(page.title, content, SUMMARY, function(data) {
					console.log('\n\n> ' + page.title + ' edited!');
				});
				/**/
			});
		});
	});

/**
	client.edit('Foo', 'Test', 'Test --~~~~', function(data) {
		console.log(data);
	});
**/
});
