var SUMMARY = 'Dodano kody pocztowe',
	regexp = /nazwa_ulicy\s?=\s?(Alej|Ulica)/;

var bot = require('../lib/bot').bot;

var client = new bot({
	server: 'poznan.wikia.com',
	//debug: true
});

// log in
client.logIn('Pyrabot', 'pyr4pyr4', function(data) {
	client.getPagesInCategory('Ulice', function(pages) {
		var i = 0;

		pages && pages.forEach(function(page) {
			// ignore pages outside main namespace
			if (page.ns != 0) {
				return;
			}

			client.getArticle(page.title, function(content) {
				if (regexp.test(content)) {
					return;
				}

				console.log("\n" + page.title);

				// dodaj kody pocztowe
				content = content.replace(regexp, "nazwa_ulicy=Ulica");

				console.log('\n\n================================\n' + page.title + '\n================================');
				console.log(content);

				// and save it
				/**
				client.edit(page.title, content, SUMMARY, function(data) {
					console.log('\n\n> ' + page.title + ' edited!');
				});
				/**/
			});
		});
	});
});
